import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { HardHat, MapPin, User } from "lucide-react";

export default function CompleteProfile() {
  const [fullName, setFullName] = useState("");
  const [suburb, setSuburb] = useState("");
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Suburb mapping function
  const mapSuburbToGroup = (selectedSuburb: string): string => {
    switch (selectedSuburb.toLowerCase()) {
      case 'seven hills':
        return 'Girraween';
      case 'parramatta':
        return 'Harris Park';
      case 'wahroonga':
      case 'girraween':
      case 'harris park':
        return selectedSuburb;
      default:
        return selectedSuburb;
    }
  };

  const suburbOptions = [
    { value: 'Wahroonga', label: 'Wahroonga' },
    { value: 'Girraween', label: 'Girraween' },
    { value: 'Seven Hills', label: 'Seven Hills (counted as Girraween)' },
    { value: 'Harris Park', label: 'Harris Park' },
    { value: 'Parramatta', label: 'Parramatta (counted as Harris Park)' },
  ];

  const updateProfileMutation = useMutation({
    mutationFn: async (data: { fullName: string; suburb: string }) => {
      const response = await apiRequest("POST", "/api/auth/complete-profile", data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
      toast({
        title: "Profile Complete!",
        description: "Welcome to Hi-Vis Vending loyalty program!",
      });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update profile",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!fullName.trim() || !suburb.trim()) {
      toast({
        title: "Required Fields",
        description: "Please fill in all required fields to continue",
        variant: "destructive",
      });
      return;
    }
    const mappedSuburb = mapSuburbToGroup(suburb);
    updateProfileMutation.mutate({ 
      fullName: fullName.trim(),
      suburb: mappedSuburb 
    });
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-orange-50 to-yellow-50 flex items-center justify-center p-4">
      <div className="max-w-md w-full">
        <Card className="border-orange-200 shadow-lg">
          <CardHeader className="text-center">
            <div className="w-16 h-16 bg-orange-500 rounded-full flex items-center justify-center mx-auto mb-4">
              <HardHat className="w-8 h-8 text-white" />
            </div>
            <CardTitle className="text-2xl text-gray-900">Complete Your Profile</CardTitle>
            <p className="text-gray-600">
              Welcome {user?.firstName || 'to Hi-Vis Vending'}! We need a bit more info to set up your account.
            </p>
          </CardHeader>

          <CardContent>
            <form onSubmit={handleSubmit} className="space-y-6">
              <div className="space-y-2">
                <Label htmlFor="fullName" className="text-gray-700 font-medium">
                  Full Name *
                </Label>
                <div className="relative">
                  <User className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
                  <Input
                    id="fullName"
                    type="text"
                    value={fullName}
                    onChange={(e) => setFullName(e.target.value)}
                    placeholder="e.g. John Smith"
                    className="pl-10 border-orange-200 focus:border-orange-500"
                    required
                  />
                </div>
              </div>

              <div className="space-y-2">
                <Label htmlFor="suburb" className="text-gray-700 font-medium">
                  Work Location *
                </Label>
                <Select value={suburb} onValueChange={setSuburb}>
                  <SelectTrigger className="border-orange-200 focus:border-orange-500">
                    <div className="flex items-center">
                      <MapPin className="w-4 h-4 text-gray-400 mr-2" />
                      <SelectValue placeholder="Select your work location" />
                    </div>
                  </SelectTrigger>
                  <SelectContent>
                    {suburbOptions.map((option) => (
                      <SelectItem key={option.value} value={option.value}>
                        {option.label}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
                <p className="text-sm text-gray-500">
                  We use this to group you with other workers in your area for local leaderboards and promotions.
                </p>
              </div>

              <Button
                type="submit"
                disabled={updateProfileMutation.isPending || !fullName.trim() || !suburb.trim()}
                className="w-full bg-orange-600 hover:bg-orange-700 text-white"
              >
                {updateProfileMutation.isPending ? "Setting up..." : "Complete Profile"}
              </Button>
            </form>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/developer.tsx.content:
import { useAuth } from "@/hooks/useAuth";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { Code2, Database, Settings, Users, Activity, AlertTriangle, Download, FileText, Camera, Upload, Edit } from "lucide-react";
import { useQuery } from "@tanstack/react-query";
import { useState } from "react";
import { useToast } from "@/hooks/use-toast";
import { User, PhotoReelItem } from "@shared/schema";
import { capitalizeName } from "@/lib/utils";

type AdminStats = {
  totalUsers: number;
  totalTransactions: number;
  pointsRedeemed: number;
  activeMachines: number;
  activeUsersToday: number;
  totalPointsEarned: number;
};

export default function Developer() {
  const { user } = useAuth() as { user: User | null };
  const { toast } = useToast();
  const [activeTab, setActiveTab] = useState("overview");

  // Get system stats
  const { data: stats } = useQuery<AdminStats>({
    queryKey: ["/api/admin/stats"],
  });

  // Get all users for developer analysis
  const { data: allUsers } = useQuery<User[]>({
    queryKey: ["/api/admin/users"],
  });

  // Get photo reel items for content management
  const { data: photoReelItems } = useQuery<PhotoReelItem[]>({
    queryKey: ["/api/content/photo-reel"],
  });

  if (!user?.isDeveloper) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-orange-50 to-yellow-50 flex items-center justify-center p-4">
        <Card className="border-red-200 shadow-lg max-w-md w-full">
          <CardHeader className="text-center">
            <div className="w-16 h-16 bg-red-500 rounded-full flex items-center justify-center mx-auto mb-4">
              <AlertTriangle className="w-8 h-8 text-white" />
            </div>
            <CardTitle className="text-2xl text-gray-900">Access Denied</CardTitle>
            <p className="text-gray-600">
              This page is restricted to developer access only.
            </p>
          </CardHeader>
        </Card>
      </div>
    );
  }

  const tabs = [
    { id: "overview", label: "Overview", icon: Activity },
    { id: "database", label: "Database", icon: Database },
    { id: "users", label: "Users", icon: Users },
    { id: "content", label: "Content", icon: Code2 },
    { id: "system", label: "System", icon: Settings },
  ];

  const handleDatabaseAction = (action: string) => {
    toast({
      title: "Developer Action",
      description: `${action} functionality would be implemented here`,
    });
  };

  const generateUserReport = () => {
    if (!allUsers) {
      toast({
        title: "No Data",
        description: "User data not available for report generation",
        variant: "destructive",
      });
      return;
    }

    const report = {
      generated: new Date().toISOString(),
      totalUsers: allUsers.length,
      usersByTier: {
        apprentice: allUsers.filter(u => u.loyaltyTier === 'apprentice').length,
        tradie: allUsers.filter(u => u.loyaltyTier === 'tradie').length,
        foreman: allUsers.filter(u => u.loyaltyTier === 'foreman').length,
      },
      usersBySuburb: allUsers.reduce((acc: Record<string, number>, user) => {
        const suburb = user.suburb || 'Unknown';
        acc[suburb] = (acc[suburb] || 0) + 1;
        return acc;
      }, {}),
      totalPoints: allUsers.reduce((sum, user) => sum + (user.totalPoints || 0), 0),
      avgPointsPerUser: allUsers.length > 0 ? 
        Math.round(allUsers.reduce((sum, user) => sum + (user.totalPoints || 0), 0) / allUsers.length) : 0,
      activeUsers: allUsers.filter(u => (u.totalPoints || 0) > 0).length,
      users: allUsers.map(user => ({
        id: user.id,
        email: user.email,
        name: `${capitalizeName(user.firstName || '')} ${capitalizeName(user.lastName || '')}`.trim(),
        suburb: user.suburb,
        tier: user.loyaltyTier,
        points: user.totalPoints,
        isAdmin: user.isAdmin,
        isDeveloper: user.isDeveloper,
        joinDate: user.createdAt,
      }))
    };

    const dataStr = JSON.stringify(report, null, 2);
    const dataBlob = new Blob([dataStr], { type: 'application/json' });
    const url = URL.createObjectURL(dataBlob);
    const link = document.createElement('a');
    link.href = url;
    link.download = `hi-vis-user-report-${new Date().toISOString().split('T')[0]}.json`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);

    toast({
      title: "Report Generated",
      description: `User report downloaded with ${allUsers.length} users`,
    });
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-orange-50 to-yellow-50 p-4">
      <div className="max-w-6xl mx-auto">
        <div className="mb-6">
          <div className="flex items-center gap-3 mb-2">
            <Code2 className="w-8 h-8 text-orange-600" />
            <h1 className="text-3xl font-bold text-gray-900">Developer Console</h1>
            <Badge variant="destructive" className="bg-red-500">RESTRICTED</Badge>
          </div>
          <p className="text-gray-600">
            Advanced system controls and diagnostics for {capitalizeName(user.firstName || '')} {capitalizeName(user.lastName || '')}
          </p>
        </div>

        {/* Tab Navigation */}
        <div className="flex space-x-1 mb-6 bg-white rounded-lg p-1 shadow-sm border border-orange-200">
          {tabs.map((tab) => {
            const Icon = tab.icon;
            return (
              <button
                key={tab.id}
                onClick={() => setActiveTab(tab.id)}
                className={`flex items-center gap-2 px-4 py-2 rounded-md transition-colors ${
                  activeTab === tab.id
                    ? "bg-orange-500 text-white"
                    : "text-gray-600 hover:bg-orange-50"
                }`}
              >
                <Icon className="w-4 h-4" />
                {tab.label}
              </button>
            );
          })}
        </div>

        {/* Tab Content */}
        <div className="space-y-6">
          {activeTab === "overview" && (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
              <Card className="border-orange-200">
                <CardHeader className="pb-2">
                  <CardTitle className="text-sm font-medium text-gray-600">Total Users</CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="text-2xl font-bold text-orange-600">{stats?.totalUsers || 0}</div>
                </CardContent>
              </Card>

              <Card className="border-orange-200">
                <CardHeader className="pb-2">
                  <CardTitle className="text-sm font-medium text-gray-600">Active Today</CardTitle>
                </CardHeader>
                <CardContent>
