import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { useAuth } from "@/hooks/useAuth";
import Navigation from "@/components/Navigation";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { HardHat, Share2, Gift, Users, Copy, QrCode } from "lucide-react";

export default function Referral() {
  const [referralCodeInput, setReferralCodeInput] = useState("");
  const [showQR, setShowQR] = useState(false);
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: myReferralData } = useQuery({
    queryKey: ["/api/referral/my-code"],
  });

  const useReferralMutation = useMutation({
    mutationFn: async (referralCode: string) => {
      const response = await apiRequest("POST", "/api/referral/use-code", { referralCode });
      return response.json();
    },
    onSuccess: (data) => {
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
      queryClient.invalidateQueries({ queryKey: ["/api/user/transactions"] });
      toast({
        title: "Welcome Bonus!",
        description: `You earned ${data.pointsEarned} points for using a referral code!`,
      });
      setReferralCodeInput("");
    },
    onError: (error) => {
      toast({
        title: "Invalid Code",
        description: error.message || "This referral code is not valid",
        variant: "destructive",
      });
    },
  });

  const handleUseReferralCode = () => {
    if (!referralCodeInput.trim()) return;
    useReferralMutation.mutate(referralCodeInput.trim().toUpperCase());
  };

  const copyReferralCode = () => {
    if (myReferralData?.referralCode) {
      navigator.clipboard.writeText(myReferralData.referralCode);
      toast({
        title: "Copied!",
        description: "Your referral code has been copied to clipboard",
      });
    }
  };

  const shareReferralCode = () => {
    if (myReferralData?.referralCode) {
      const message = `Join Hi-Vis Vending loyalty program with my code ${myReferralData.referralCode} and get 25 free points! ðŸ—ï¸`;

      if (navigator.share) {
        navigator.share({
          title: 'Join Hi-Vis Vending',
          text: message,
          url: window.location.origin
        });
      } else {
        // Fallback for browsers without Web Share API
        navigator.clipboard.writeText(message);
        toast({
          title: "Copied!",
          description: "Referral message copied to clipboard",
        });
      }
    }
  };

  const myReferralCode = myReferralData?.referralCode;
  const hasUsedReferral = user?.referredBy;

  return (
    <div className="min-h-screen bg-slate-800">
      {/* Header */}
      <header className="bg-slate-800 border-b-4 border-orange-500 sticky top-0 z-50">
        <div className="flex items-center justify-center px-4 py-3">
          <div className="flex items-center space-x-3">
            <div className="w-12 h-12 bg-orange-500 rounded-lg flex items-center justify-center">
              <HardHat className="text-slate-800 w-6 h-6" />
            </div>
            <div>
              <h1 className="text-white font-bold text-lg">Refer-a-Mate</h1>
              <p className="text-yellow-400 text-xs font-medium">Share the rewards with your crew</p>
            </div>
          </div>
        </div>
      </header>

      <main className="p-6 pb-20">
        {/* Hero Section */}
        <div className="gradient-orange rounded-2xl p-6 text-slate-800 mb-6">
          <div className="text-center">
            <Users className="w-16 h-16 mx-auto mb-3" />
            <h2 className="text-2xl font-bold mb-2">Bring Your Mates!</h2>
            <p className="text-lg opacity-90">Share your code, earn together</p>
          </div>
        </div>

        {/* How it Works */}
        <section className="mb-6">
          <h3 className="text-white text-lg font-semibold mb-4">How It Works:</h3>
          <div className="space-y-3">
            <Card className="bg-slate-700 border-slate-600">
              <CardContent className="p-4 flex items-center space-x-4">
                <div className="w-10 h-10 bg-orange-500 rounded-full flex items-center justify-center text-slate-800 font-bold">
                  1
                </div>
                <div>
                  <p className="font-semibold text-white">Share your code</p>
                  <p className="text-sm text-slate-400">Send your unique code to workmates</p>
                </div>
              </CardContent>
            </Card>

            <Card className="bg-slate-700 border-slate-600">
              <CardContent className="p-4 flex items-center space-x-4">
                <div className="w-10 h-10 bg-yellow-400 rounded-full flex items-center justify-center text-slate-800 font-bold">
                  2
                </div>
                <div>
                  <p className="font-semibold text-white">They join & earn</p>
                  <p className="text-sm text-slate-400">New mate gets 25 welcome points</p>
                </div>
              </CardContent>
            </Card>

            <Card className="bg-slate-700 border-slate-600">
              <CardContent className="p-4 flex items-center space-x-4">
                <div className="w-10 h-10 bg-green-500 rounded-full flex items-center justify-center text-white font-bold">
                  3
                </div>
                <div>
                  <p className="font-semibold text-white">You get rewarded</p>
                  <p className="text-sm text-slate-400">Earn 50 points for each referral</p>
                </div>
              </CardContent>
            </Card>
          </div>
        </section>

        {/* My Referral Code */}
        <section className="mb-6">
          <h3 className="text-white text-lg font-semibold mb-4">Your Referral Code</h3>
          <Card className="bg-white border-slate-200">
            <CardContent className="p-6">
              <div className="text-center">
                <div className="safety-stripes w-16 h-4 rounded mx-auto mb-4"></div>
                <div className="text-4xl font-bold text-slate-800 mb-2 tracking-wider">
                  {myReferralCode || "LOADING..."}
                </div>
                <p className="text-slate-600 mb-4">Share this code with your workmates</p>

                <div className="flex space-x-2 justify-center">
                  <Button
                    onClick={copyReferralCode}
                    disabled={!myReferralCode}
                    className="bg-orange-500 hover:bg-orange-600 text-slate-800"
                  >
                    <Copy className="w-4 h-4 mr-2" />
                    Copy Code
                  </Button>

                  <Button
                    onClick={shareReferralCode}
                    disabled={!myReferralCode}
                    className="bg-yellow-400 hover:bg-yellow-500 text-slate-800"
                  >
                    <Share2 className="w-4 h-4 mr-2" />
                    Share
                  </Button>
                </div>

                <div className="mt-4">
                  <Button
                    onClick={() => setShowQR(!showQR)}
                    variant="outline"
                    className="text-slate-600 border-slate-300"
                  >
                    <QrCode className="w-4 h-4 mr-2" />
                    {showQR ? "Hide" : "Show"} QR Code
                  </Button>
                </div>

                {showQR && myReferralCode && (
                  <div className="mt-4 p-4 bg-slate-100 rounded-lg">
                    <div className="w-32 h-32 bg-slate-300 rounded-lg mx-auto flex items-center justify-center">
                      <QrCode className="w-16 h-16 text-slate-600" />
                    </div>
                    <p className="text-xs text-slate-500 mt-2">Scan to join with code {myReferralCode}</p>
                  </div>
                )}
              </div>
            </CardContent>
          </Card>
        </section>

        {/* Use Referral Code */}
        {!hasUsedReferral && (
          <section className="mb-6">
            <h3 className="text-white text-lg font-semibold mb-4">Got a Mate's Code?</h3>
            <Card className="bg-slate-700 border-slate-600">
              <CardContent className="p-6">
                <div className="space-y-4">
                  <div>
                    <Label htmlFor="referralCode" className="text-white">
                      Enter Referral Code
                    </Label>
                    <Input
                      id="referralCode"
                      value={referralCodeInput}
                      onChange={(e) => setReferralCodeInput(e.target.value.toUpperCase())}
                      placeholder="Enter 6-character code"
                      maxLength={6}
                      className="mt-1 bg-slate-800 border-slate-600 text-white"
                    />
                  </div>

                  <Button
                    onClick={handleUseReferralCode}
                    disabled={!referralCodeInput.trim() || useReferralMutation.isPending}
                    className="w-full bg-green-600 hover:bg-green-700 text-white"
                  >
                    <Gift className="w-4 h-4 mr-2" />
                    {useReferralMutation.isPending ? "Checking..." : "Use Code & Get 25 Points"}
                  </Button>
                </div>
              </CardContent>
            </Card>
          </section>
        )}

        {hasUsedReferral && (
          <section className="mb-6">
            <Card className="bg-green-600 border-green-500">
              <CardContent className="p-6 text-center">
                <Gift className="w-12 h-12 text-white mx-auto mb-3" />
                <h3 className="text-white font-semibold mb-2">Welcome Bonus Claimed!</h3>
                <p className="text-green-100">You've already used a referral code and earned your welcome points.</p>
              </CardContent>
            </Card>
          </section>
        )}

        {/* Referral Stats - Moved to Bottom */}
        <section>
          <h3 className="text-white text-lg font-semibold mb-4">Your Referrals</h3>
          <div className="grid grid-cols-2 gap-4">
            <Card className="bg-slate-700 border-slate-600">
              <CardContent className="p-4 text-center">
                <p className="text-3xl font-bold text-orange-500">{user?.referralCount || 0}</p>
                <p className="text-slate-300">Mates Referred</p>
              </CardContent>
            </Card>

            <Card className="bg-slate-700 border-slate-600">
              <CardContent className="p-4 text-center">
                <p className="text-3xl font-bold text-green-400">{(user?.referralCount || 0) * 50}</p>
                <p className="text-slate-300">Bonus Points</p>
              </CardContent>
            </Card>
          </div>
        </section>
      </main>

      <Navigation />
    </div>
  );
}

client/src/pages/reset-password.tsx.content:
import { useState, useEffect } from "react";
import { useMutation } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { HardHat, Lock, CheckCircle } from "lucide-react";
import { Link, useLocation } from "wouter";

export default function ResetPassword() {
  const [formData, setFormData] = useState({
    password: "",
    confirmPassword: "",
  });
  const [token, setToken] = useState("");
  const [isComplete, setIsComplete] = useState(false);
  const [location] = useLocation();
  const { toast } = useToast();

  // Extract token from URL
  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const tokenFromUrl = urlParams.get('token');
    if (tokenFromUrl) {
      setToken(tokenFromUrl);
    } else {
      toast({
        title: "Invalid Reset Link",
        description: "This password reset link is invalid or has expired",
        variant: "destructive",
      });
    }
  }, [location, toast]);

  const resetMutation = useMutation({
    mutationFn: async (data: { token: string; password: string }) => {
      const response = await apiRequest("POST", "/api/auth/reset-password", data);
      return response.json();
    },
    onSuccess: () => {
      setIsComplete(true);
      toast({
        title: "Password Reset Successfully",
        description: "You can now log in with your new password.",
      });
    },
    onError: (error) => {
      toast({
        title: "Reset Failed",
        description: error.message || "Failed to reset password. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.password.trim() || !formData.confirmPassword.trim()) {
      toast({
        title: "Required Fields",
        description: "Please fill in both password fields",
        variant: "destructive",
      });
      return;
    }

    if (formData.password !== formData.confirmPassword) {
      toast({
        title: "Password Mismatch",
        description: "Passwords do not match",
        variant: "destructive",
      });
      return;
    }

    if (formData.password.length < 6) {
      toast({
        title: "Password Too Short",
        description: "Password must be at least 6 characters long",
        variant: "destructive",
      });
      return;
    }

    if (!token) {
      toast({
        title: "Invalid Token",
        description: "Reset token is missing or invalid",
        variant: "destructive",
      });
      return;
    }

    resetMutation.mutate({ token, password: formData.password });
  };

  const handleInputChange = (field: string, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  if (isComplete) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-orange-50 to-yellow-50 flex items-center justify-center p-4">
        <div className="max-w-md w-full">
          <Card className="border-orange-200 shadow-lg">
            <CardHeader className="text-center">
              <div className="w-16 h-16 bg-green-500 rounded-full flex items-center justify-center mx-auto mb-4">
                <CheckCircle className="w-8 h-8 text-white" />
              </div>
              <CardTitle className="text-2xl text-gray-900">Password Reset Complete</CardTitle>
              <p className="text-gray-600">
                Your password has been successfully updated
              </p>
            </CardHeader>

            <CardContent>
              <Link href="/login">
                <Button className="w-full bg-orange-600 hover:bg-orange-700 text-white">
                  Continue to Login
                </Button>
              </Link>
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  if (!token) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-orange-50 to-yellow-50 flex items-center justify-center p-4">
        <div className="max-w-md w-full">
          <Card className="border-orange-200 shadow-lg">
            <CardHeader className="text-center">
              <div className="w-16 h-16 bg-red-500 rounded-full flex items-center justify-center mx-auto mb-4">
                <HardHat className="w-8 h-8 text-white" />
              </div>
              <CardTitle className="text-2xl text-gray-900">Invalid Reset Link</CardTitle>
              <p className="text-gray-600">
                This password reset link is invalid or has expired
              </p>
            </CardHeader>

            <CardContent>
              <Link href="/forgot-password">
                <Button className="w-full bg-orange-600 hover:bg-orange-700 text-white">
                  Request New Reset Link
                </Button>
              </Link>
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-orange-50 to-yellow-50 flex items-center justify-center p-4">
      <div className="max-w-md w-full">
        <Card className="border-orange-200 shadow-lg">
          <CardHeader className="text-center">
            <div className="w-16 h-16 bg-orange-500 rounded-full flex items-center justify-center mx-auto mb-4">
              <HardHat className="w-8 h-8 text-white" />
            </div>
            <CardTitle className="text-2xl text-gray-900">Set New Password</CardTitle>
            <p className="text-gray-600">
              Choose a strong password for your account
            </p>
          </CardHeader>

          <CardContent>
            <form onSubmit={handleSubmit} className="space-y-6">
              <div className="space-y-2">
                <Label htmlFor="password" className="text-gray-700 font-medium">
                  New Password
                </Label>
                <div className="relative">
                  <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
                  <Input
                    id="password"
                    type="password"
                    value={formData.password}
                    onChange={(e) => handleInputChange("password", e.target.value)}
                    placeholder="At least 6 characters"
                    className="pl-10 border-orange-200 focus:border-orange-500"
                    required
                  />
                </div>
              </div>

              <div className="space-y-2">
                <Label htmlFor="confirmPassword" className="text-gray-700 font-medium">
                  Confirm New Password
                </Label>
                <div className="relative">
                  <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
                  <Input
                    id="confirmPassword"
                    type="password"
                    value={formData.confirmPassword}
                    onChange={(e) => handleInputChange("confirmPassword", e.target.value)}
                    placeholder="Confirm your new password"
                    className="pl-10 border-orange-200 focus:border-orange-500"
                    required
                  />
                </div>
              </div>

              <Button
                type="submit"
                disabled={resetMutation.isPending}
                className="w-full bg-orange-600 hover:bg-orange-700 text-white"
              >
                {resetMutation.isPending ? "Updating Password..." : "Update Password"}
              </Button>
            </form>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/rewards.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { useAuth } from "@/hooks/useAuth";
import Navigation from "@/components/Navigation";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { HardHat, Coffee, Cookie, Gift, Copy, Check } from "lucide-react";

export default function Rewards() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // State for redemption modal
  const [showRedemptionModal, setShowRedemptionModal] = useState(false);
  const [redemptionCode, setRedemptionCode] = useState("");

  const { data: rewards = [] } = useQuery({
    queryKey: ["/api/rewards"],
  });

  // Clipboard state
  const [copiedCode, setCopiedCode] = useState(false);

  const redeemMutation = useMutation({
    mutationFn: async (rewardId: string) => {
      const response = await apiRequest("POST", "/api/rewards/redeem", { rewardId });
      return response.json();
    },
    onSuccess: (data) => {
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
      queryClient.invalidateQueries({ queryKey: ["/api/user/transactions"] });
      setRedemptionCode(data.redemptionCode);
      setShowRedemptionModal(true);
      toast({
        title: "Reward Redeemed!",
        description: `Your redemption code is: ${data.redemptionCode}`,
      });
    },
    onError: (error) => {
      toast({
        title: "Redemption Failed",
        description: error.message || "Failed to redeem reward",
        variant: "destructive",
      });
    },
  });

  const copyToClipboard = () => {
    navigator.clipboard.writeText(redemptionCode);
    setCopiedCode(true);
    setTimeout(() => setCopiedCode(false), 2000);
    toast({
      title: "Code Copied!",
      description: "Redemption code copied to clipboard",
    });
  };

  const getRewardIcon = (category: string) => {
    switch (category) {
      case 'drink':
        return <Coffee className="text-white w-6 h-6" />;
      case 'snack':
        return <Cookie className="text-white w-6 h-6" />;
      default:
        return <Gift className="text-white w-6 h-6" />;
    }
  };

  const getRewardColor = (category: string) => {
    switch (category) {
      case 'drink':
        return 'bg-blue-500';
      case 'snack':
        return 'bg-orange-500';
      default:
        return 'bg-green-500';
    }
  };

  const userPoints = user?.totalPoints || 0;

  return (
    <div className="min-h-screen bg-slate-800">
      {/* Header */}
      <header className="bg-slate-800 border-b-4 border-orange-500 sticky top-0 z-50">
        <div className="flex items-center justify-center px-4 py-3">
          <div className="flex items-center space-x-3">
            <div className="w-12 h-12 bg-orange-500 rounded-lg flex items-center justify-center">
              <HardHat className="text-slate-800 w-6 h-6" />
            </div>
            <div>
              <h1 className="text-white font-bold text-lg">Rewards</h1>
              <p className="text-yellow-400 text-xs font-medium">{userPoints} points available</p>
            </div>
          </div>
        </div>
      </header>

      <main className="p-6 pb-20">
        {/* Points Balance */}
        <div className="gradient-orange rounded-2xl p-6 text-slate-800 mb-6">
          <div className="text-center">
            <h2 className="text-3xl font-bold">{userPoints}</h2>
            <p className="text-lg opacity-90">Available Points</p>
          </div>
        </div>

        {/* Available Rewards */}
        <section>
          <h3 className="text-white text-lg font-semibold mb-4">Available Rewards</h3>

          {rewards.length === 0 ? (
            <Card className="bg-slate-700 border-slate-600">
              <CardContent className="p-6 text-center">
                <Gift className="w-12 h-12 text-slate-400 mx-auto mb-3" />
                <p className="text-slate-300">No rewards available at the moment</p>
              </CardContent>
            </Card>
          ) : (
            <div className="space-y-3">
              {rewards.map((reward: any) => {
                const canAfford = userPoints >= reward.pointsCost;
                const isRedeeming = redeemMutation.isPending;

                return (
                  <Card key={reward.id} className={`bg-slate-700 border-slate-600 ${!canAfford ? 'opacity-50' : ''}`}>
                    <CardContent className="p-4">
                      <div className="flex items-center justify-between">
                        <div className="flex items-center space-x-4">
                          <div className={`w-12 h-12 ${getRewardColor(reward.category)} rounded-full flex items-center justify-center`}>
                            {getRewardIcon(reward.category)}
                          </div>
                          <div>
                            <p className="font-semibold text-white">{reward.name}</p>
                            <p className="text-sm text-slate-400">{reward.pointsCost} points required</p>
                            {reward.description && (
                              <p className="text-xs text-slate-500 mt-1">{reward.description}</p>
                            )}
                          </div>
                        </div>
                        <Button
                          onClick={() => redeemMutation.mutate(reward.id)}
                          disabled={!canAfford || isRedeeming}
                          className={`${
                            canAfford 
                              ? "bg-orange-500 hover:bg-orange-600 text-slate-800" 
                              : "bg-slate-600 text-slate-400 cursor-not-allowed"
                          } font-semibold px-4 py-2`}
                        >
                          {isRedeeming ? "Redeeming..." : canAfford ? "Redeem" : "Insufficient"}
                        </Button>
                      </div>
                    </CardContent>
                  </Card>
                );
              })}
            </div>
          )}
        </section>

        {/* Redemption Tips */}
        <section className="mt-8">
          <Card className="bg-slate-700 border-slate-600">
            <CardContent className="p-6">
              <h3 className="text-white font-semibold mb-3">How to earn more points:</h3>
              <ul className="text-slate-300 space-y-2">
                <li className="flex items-center space-x-2">
                  <span className="w-2 h-2 bg-orange-500 rounded-full"></span>
                  <span>Scan QR codes at vending machines (+10 points)</span>
                </li>
                <li className="flex items-center space-x-2">
                  <span className="w-2 h-2 bg-yellow-400 rounded-full"></span>
                  <span>Complete your punch card (+100 points)</span>
                </li>
                <li className="flex items-center space-x-2">
                  <span className="w-2 h-2 bg-green-500 rounded-full"></span>
                  <span>Achieve higher loyalty tiers for bonuses</span>
                </li>
              </ul>
            </CardContent>
          </Card>
        </section>

        {/* Referral Section */}
        <section className="mt-8">
          <h3 className="text-white text-lg font-semibold mb-4 flex items-center">
            <Gift className="w-5 h-5 mr-2" />
            Earn More Points
          </h3>

          {/* Referral Rewards */}
          <Card className="bg-slate-700 border-slate-600 mb-4">
            <CardContent className="p-6">
              <div className="text-center">
                <h4 className="text-white font-semibold mb-2">Invite Your Workmates!</h4>
                <p className="text-slate-300 text-sm mb-4">
                  Share Hi-Vis Vending with friends and earn bonus points when they sign up.
                </p>
                <div className="grid grid-cols-2 gap-4 mb-4">
                  <div className="bg-slate-800 rounded-lg p-3">
                    <p className="text-orange-400 font-bold text-lg">+25</p>
                    <p className="text-slate-400 text-xs">Points for you</p>
                  </div>
                  <div className="bg-slate-800 rounded-lg p-3">
                    <p className="text-green-400 font-bold text-lg">+25</p>
                    <p className="text-slate-400 text-xs">Points for them</p>
                  </div>
                </div>
                <Button 
                  onClick={() => window.location.href = '/referral'}
                  className="w-full bg-orange-600 hover:bg-orange-700 text-white"
                >
                  <Gift className="w-4 h-4 mr-2" />
                  View My Referral Code
                </Button>
              </div>
            </CardContent>
          </Card>
        </section>
      </main>

      <Navigation />

      {/* Redemption Modal */}
      <Dialog open={showRedemptionModal} onOpenChange={setShowRedemptionModal}>
        <DialogContent className="bg-slate-700 border-slate-600">
          <DialogHeader>
            <DialogTitle className="text-white flex items-center">
              <Gift className="w-5 h-5 mr-2 text-orange-500" />
              Reward Redeemed Successfully!
            </DialogTitle>
          </DialogHeader>
          <div className="space-y-4">
            <div className="text-center">
              <p className="text-slate-300 mb-4">
                Show this code at any Hi-Vis vending machine to claim your reward:
              </p>
              <div className="bg-slate-800 p-4 rounded-lg border-2 border-orange-500">
                <div className="text-2xl font-mono text-orange-400 font-bold tracking-wider">
                  {redemptionCode}
                </div>
              </div>
            </div>

            <div className="flex space-x-2">
              <Button
                onClick={copyToClipboard}
                className="flex-1 bg-orange-600 hover:bg-orange-700 text-white"
              >
                {copiedCode ? (
                  <>
                    <Check className="w-4 h-4 mr-2" />
                    Copied!
                  </>
                ) : (
                  <>
                    <Copy className="w-4 h-4 mr-2" />
                    Copy Code
                  </>
                )}
              </Button>
              <Button
                onClick={() => setShowRedemptionModal(false)}
                variant="outline"
                className="border-slate-600 text-white hover:bg-slate-600"
              >
                Close
              </Button>
            </div>

            <div className="text-xs text-slate-400 text-center">
              ðŸ’¡ Pro Tip: Screenshot this code or copy it to use at the vending machine
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/pages/scan-old.tsx.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { HardHat, QrCode, CheckCircle, ArrowLeft } from "lucide-react";
import { Link } from "wouter";

export default function Scan() {
  const [isScanning, setIsScanning] = useState(false);
  const [scanResult, setScanResult] = useState<any>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const scanMutation = useMutation({
    mutationFn: async (qrData: string) => {
      const response = await apiRequest("POST", "/api/qr/scan", { qrData });
      return response.json();
    },
    onSuccess: (data) => {
      setScanResult(data);
      setIsScanning(false);
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
      queryClient.invalidateQueries({ queryKey: ["/api/user/transactions"] });
      toast({
        title: "Success!",
        description: `Earned ${data.pointsEarned} points from your purchase!`,
      });
    },
    onError: (error) => {
      setIsScanning(false);
      toast({
        title: "Scan Failed",
        description: error.message || "Invalid QR code",
        variant: "destructive",
      });
    },
  });

  const handleScan = (data: string) => {
    if (data) {
      scanMutation.mutate(data);
    }
  };

  const handleStartScan = () => {
    setIsScanning(true);
    setScanResult(null);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-orange-50 to-yellow-50 p-4">
      {/* Header */}
      <div className="max-w-lg mx-auto">
        <div className="flex items-center gap-4 mb-6 pt-4">
          <Link href="/">
            <Button variant="outline" size="icon" className="border-orange-200">
              <ArrowLeft className="w-4 h-4" />
            </Button>
          </Link>
          <div>
            <h1 className="text-2xl font-bold text-gray-900">QR Scanner</h1>
            <p className="text-gray-600">Scan machine codes to earn points</p>
          </div>
        </div>

        {/* Info Card */}
        <Card className="mb-6 border-orange-200">
          <CardContent className="p-6 text-center">
            <QrCode className="w-12 h-12 mx-auto mb-4 text-orange-600" />
            <h2 className="text-xl font-semibold mb-2">Looking for your personal QR code?</h2>
            <p className="text-gray-600 mb-4">
              Each customer now has their own unique QR code for instant promotions at vending machines.
            </p>
            <Link href="/my-code">
              <Button className="bg-orange-600 hover:bg-orange-700 text-white">
                Go to My Code
              </Button>
            </Link>
          </CardContent>
        </Card>

        {/* Legacy Scanner */}
        <Card className="border-orange-200">
          <CardContent className="p-6">
            <h3 className="text-lg font-semibold mb-4">Manual Points Entry</h3>
            <p className="text-gray-600 mb-4">
              Use this to manually add points for purchases (fallback method).
            </p>

            {!isScanning && !scanResult && (
              <Button 
                onClick={handleStartScan}
                className="w-full bg-orange-600 hover:bg-orange-700 text-white"
              >
                Start Manual Scanner
              </Button>
            )}

            {scanResult && (
              <div className="text-center">
                <CheckCircle className="w-12 h-12 mx-auto mb-4 text-green-600" />
                <h3 className="text-lg font-semibold mb-2">Points Added!</h3>
                <p className="text-gray-600">
                  You earned {scanResult.pointsEarned} points from your purchase.
                </p>
              </div>
            )}
          </CardContent>
        </Card>
